INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2556, 14, 163, N'Post-Fire Restoration ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Following a wildfire, post-fire restoration focuses on rehabilitating the ecosystem to restore its functionality and biodiversity. This includes activities such as reseeding native plant species, planting trees, implementing erosion control measures to stabilize soils, as well as salvage logging and site preparation.</span></p>', NULL, N'#358e2b', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2557, 14, 163, N'Fuels Reduction ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Fuels reduction efforts encompass a range of projects aimed at mitigating the risk and severity of wildfires by reducing the amount and continuity of flammable vegetation. These projects typically involve activities such as thinning dense vegetation, removing hazardous fuels, conducting prescribed burns, and creating defensible spaces around structures. The primary objectives of fuels reduction projects are to enhance forest resilience, protect communities from wildfire threats, and restore fire-adapted ecosystems to a healthier state. By strategically managing vegetation and fuel loads, these projects aim to minimize the intensity and spread of wildfires, thereby safeguarding both human communities and natural landscapes.</span></p>', NULL, N'#38a343', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2558, 14, 163, N'Integrated Pest and Disease Management ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Proactively addresses forest threats from pests and diseases using a combination of biological control, cultural practices, and targeted chemical treatments. It aims to maintain ecosystem balance and reduce vulnerability to outbreaks.</span></p>', NULL, N'#44874b', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2559, 14, 163, N'Aspen Regeneration ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Targets the regeneration of aspen, a specific tree species, which may involve activities like planting or managing conditions for natural regeneration. Includes protecting aspen groves from decline from conifer encroachment resulting from changes in natural fire and hydrology regimes.</span></p>', NULL, N'#15631d', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2560, 14, 164, N'Community Risk Reduction and Adaptation Planning ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Involves comprehensive planning and implementation of strategies aimed at reducing community vulnerability to wildfire risks and adapting to potential wildfire impacts. This includes conducting wildfire risk assessments to identify areas of vulnerability, developing community-wide emergency response plans, and implementing measures to improve evacuation routes and access for residents during wildfire events.</span><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Segoe UI''; color: black; mso-color-alt: windowtext; background: white; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;"> </span><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Additionally, this strategy involves engaging community members through education and outreach programs to raise awareness about wildfire risks and promote proactive measures to reduce fire hazards.</span></p>', NULL, N'#4e861e', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2561, 14, 164, N'Wildland Urban Interface Hazardous Fuel Reduction ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Focuses on mitigating wildfire risks by reducing hazardous fuels in areas where urban development meets wildland vegetation. This strategy involves targeted efforts to thin out dense vegetation, remove dead or diseased trees, and create firebreaks to decrease the likelihood of wildfire ignition and spread. By reducing fuel loads near communities, this approach aims to enhance wildfire resilience and protect lives, property, and ecosystems in the wildland-urban interface.</span></p>', NULL, N'#78936a', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2562, 14, 164, N'Defensible Space and Home Hardening ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Initiatives focus on creating fire-resilient environments around homes and structures to mitigate the risk of wildfire spread and home ignition. These projects typically involve implementing and supporting programs that encourage homeowners to create defensible space by clearing vegetation and combustible materials from around their properties. Additionally, home hardening efforts involve retrofitting structures with fire-resistant materials, such as ember-resistant vents, flame-retardant roofing, and non-combustible siding, to reduce vulnerability to wildfire impacts. By promoting defensible space and home hardening measures, this strategy aims to enhance community wildfire resilience and protect lives and property in fire-prone areas.</span></p>', NULL, N'#c9e54a', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2563, 14, 164, N'Firefighting Infrastructure Enhancement ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Focuses on bolstering firefighting capabilities by upgrading essential infrastructure and resources. This includes initiatives to improve equipment, facilities, water sources, and access roads crucial for effective wildfire response. Specific projects under this strategy may involve the construction or renovation of fire stations, installation of water storage tanks or hydrants, upgrading firefighting vehicles and equipment, and maintaining or expanding access roads to enhance emergency response capabilities in wildfire-prone areas. By investing in firefighting infrastructure, this strategy aims to enhance the efficiency and effectiveness of wildfire suppression efforts, ultimately reducing the impact of wildfires on communities and ecosystems.</span></p>', NULL, N'#809d4d', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2564, 14, 165, N'Hydrologic Process & Geomorphic Feature Restoration ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Involves restoring natural hydrological processes and geomorphic features within riparian zones. This may include efforts to re-establish water flow patterns, groundwater interactions, and physical landforms to enhance the overall health of the riparian ecosystem.</span></p>', NULL, N'#3800d9', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2565, 14, 165, N'Vegetation Re-establishment ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Focuses on reintroducing or enhancing vegetation within the riparian zone. This can include planting native vegetation, restoring habitat for plant communities, and promoting biodiversity.</span></p>', NULL, N'#97c1e8', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2566, 14, 165, N'Floodplain Re-connection ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Aims to restore the connection between the riparian zone and its adjacent floodplain. This can involve removing barriers, altering land use practices, or implementing other measures to allow natural flooding and ecological processes to occur.</span></p>', NULL, N'#67c5e0', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2567, 14, 166, N'Water Quality Monitoring and Assessment', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">This approach involves regular sampling and analysis of water samples to detect pollutants, measure nutrient levels, and assess overall water health. Projects under this strategy may include establishing monitoring stations at key water bodies, conducting routine water sampling campaigns, employing advanced analytical techniques to identify contaminants, and collaborating with stakeholders to interpret data and develop strategies for water quality improvement. By continuously monitoring and assessing water quality, this strategy aims to safeguard aquatic ecosystems, protect public health, and inform evidence-based management decisions for sustainable water resource management.</span></p>', NULL, N'#5740c1', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2568, 14, 166, N'Stormwater Management ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Encompasses the implementation of proactive measures and practices to effectively control and mitigate the impact of stormwater runoff on water quality and hydrological systems. This includes the deployment of various strategies such as green infrastructure, detention basins, permeable pavements, and vegetated swales to manage stormwater at its source. Specific projects falling under this strategy may involve the installation of rain gardens, bioswales, and constructed wetlands to capture and treat stormwater, the construction of retention ponds or infiltration trenches to slow down runoff and enhance groundwater recharge, and the implementation of erosion control measures to prevent sedimentation and pollutant transport into water bodies.</span></p>', NULL, N'#2984c1', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2569, 14, 166, N'Road Maintenance/Decommissioning ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">This strategy involves regular inspection and maintenance of roads to prevent erosion, sedimentation, and other water quality issues resulting from inadequate drainage or surface erosion. Projects under this strategy may include repairing drainage structures, stabilizing road surfaces, installing erosion control measures such as culverts or gabions, and revegetating road shoulders to prevent soil erosion. Additionally, where roads are deemed unnecessary or environmentally damaging, decommissioning efforts may involve restoring disturbed areas to their natural state, removing road infrastructure, and implementing measures to minimize soil disturbance and erosion.</span></p>', NULL, N'#917dd0', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2570, 14, 166, N'Enhance/Restore Groundwater Storage ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Endeavors to improve the capacity of underground aquifers to store and replenish groundwater resources. Projects under this strategy may include the construction of recharge facilities such as infiltration basins, injection wells, or recharge ponds to replenish aquifers with surface water or treated wastewater. Additionally, efforts may focus on protecting and preserving natural recharge areas, such as wetlands, riparian zones, and permeable soils, to facilitate the natural infiltration of precipitation into aquifers. Furthermore, initiatives may involve promoting water conservation practices, reducing groundwater extraction rates, and implementing land use planning strategies to minimize groundwater depletion and contamination.</span></p>', NULL, N'#4c27c1', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2571, 14, 167, N'Mechanical and Manual Removal ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Involves physically removing invasive species or managing native species through manual efforts such as hand-pulling, cutting, or other mechanical methods. Additionally, efforts may involve the use of specialized tools or machinery for larger-scale removal operations in areas where invasive species have proliferated extensively.</span></p>', NULL, N'#ccbb62', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2572, 14, 167, N'Chemical Control (Herbicides) ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Involves the targeted application of herbicides or other chemical treatments to manage invasive species or control the growth of undesirable vegetation.<strong> </strong>Projects under this strategy may include the application of herbicides using precision techniques such as spot spraying or controlled spraying to eradicate invasive plants in sensitive ecosystems. Additionally, efforts may involve the use of herbicide treatments in restoration areas to suppress the growth of invasive species and facilitate the establishment of native vegetation. Furthermore, projects may include research and monitoring activities to assess the effectiveness of chemical treatments and their long-term impacts on ecosystem health.</span></p>', NULL, N'#f6a16c', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2573, 14, 168, N'Wildlife Corridor Development ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Focuses on establishing and improving pathways that enable the movement of wildlife across diverse habitats. By creating interconnected corridors, this strategy facilitates the natural migration, foraging, and breeding patterns of various species, contributing to enhanced biodiversity and ecosystem resilience. Projects within this strategy may include habitat restoration efforts to reconnect fragmented landscapes, construction of wildlife bridges or tunnels to bypass barriers such as roads or developments, and the establishment of conservation easements to protect critical wildlife corridors from development pressures. Additionally, initiatives may involve community engagement and collaboration with landowners to secure wildlife-friendly land use practices along corridor routes.</span></p>', NULL, N'#eae047', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2574, 14, 168, N'Urban Pollinator Landscaping ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Focuses on transforming urban environments into vibrant habitats that nurture pollinator species like bees, butterflies, and other beneficial insects. This strategy involves redesigning urban landscapes to prioritize native flowering plants, which serve as essential food and nesting resources for pollinators. Projects within this strategy may include the creation of pollinator-friendly gardens, installation of native plant corridors along urban green spaces, and establishment of community gardens or rooftop beekeeping initiatives. Additionally, initiatives may promote public education and outreach to raise awareness about the importance of pollinators and encourage community participation in pollinator conservation efforts. By fostering pollinator-friendly urban environments, this strategy aims to enhance urban biodiversity, promote ecosystem health, and strengthen pollinator populations critical for food production and ecosystem functioning.</span></p>', NULL, N'#cb5d00', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2575, 14, 169, N'Reintroduction Programs ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Focuses on the restoration of species that have faced local extinction or are endangered by reintroducing them into their native habitats. This strategy involves meticulous planning and execution to ensure the successful acclimation of species back into their natural ecosystems. Projects within this strategy may include captive breeding programs to increase population numbers, habitat restoration efforts to create suitable environments for reintroduction, and comprehensive monitoring to assess the success and impact of reintroduced populations.</span></p>', NULL, N'#96642f', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2576, 14, 169, N'Genetic Management ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">This strategy employs various techniques, including selective breeding and genetic interventions, to maintain or enhance the overall genetic health of the species. Projects within this strategy may involve genetic studies to assess population diversity, breeding programs to introduce genetic variation, and genetic testing to identify and mitigate risks of inbreeding or genetic diseases. Additionally, conservation efforts may include habitat restoration to support genetic diversity and connectivity among populations.</span></p>', NULL, N'#c1aa00', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2577, 14, 169, N'Disease Management ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Focuses on addressing diseases that impact specific species populations. This strategy involves a range of actions, including disease monitoring, prevention, and treatment, aimed at safeguarding and enhancing the health of the population. Projects within this strategy may include disease surveillance programs to monitor the prevalence and spread of pathogens, implementing measures to prevent disease transmission, such as habitat management or quarantine protocols, and developing treatments or vaccines to control disease outbreaks. Additionally, research efforts may focus on understanding the underlying causes of disease susceptibility and developing strategies to enhance population resilience.</span></p>', NULL, N'#da8f3e', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2578, 14, 169, N'Assisted Migration Programs ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Focuses on the relocation of species or populations to suitable habitats where they historically existed or are predicted to thrive under changing environmental conditions. This strategy involves careful assessment of habitat suitability, species'' ecological requirements, and potential impacts on existing ecosystems. Projects under this strategy may include translocation efforts to move species to more suitable habitats, habitat restoration to prepare new areas for colonization, and monitoring programs to assess the success of translocation efforts and the establishment of relocated populations. Additionally, research initiatives may focus on understanding the potential consequences of assisted migration on native species and ecosystems, as well as developing guidelines and best practices for successful relocation projects.</span></p>', NULL, N'#ffc200', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2579, 14, 169, N'Restore/Reconnect Habitat ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo sans'',serif; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Focuses on restoring and reconnecting habitats to provide suitable environments for species, promoting movement, breeding, and overall survival. This strategy encompasses efforts both in aquatic and terrestrial ecosystems. It includes projects aimed at removing barriers like roads or dams to facilitate the natural movement of species across landscapes. Furthermore, the strategy emphasizes the design and implementation of aquatic organism passage structures, such as fish ladders and culverts, to aid the movement of aquatic species through waterways.</span></p>', NULL, N'#be8d36', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2580, 14, 170, N'Biofuel Production', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo Sans''; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Focuses on harnessing biomass resources from forestry operations, agricultural residues, and dedicated energy crops to produce renewable fuels. These strategies involve establishing bioenergy facilities near forested areas to utilize woody biomass, forest residues, and by-products from timber processing for biofuel production. Projects may include constructing biomass power plants, pellet mills, or bio-refineries that convert forest biomass into ethanol, biodiesel, or bio-oil. By integrating biofuel production with sustainable forestry practices, these approaches offer alternative energy sources, promote forest health, reduce wildfire risk through biomass utilization, and contribute to carbon sequestration by utilizing waste materials for energy production.</span></p>', NULL, N'#7f1616', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2581, 14, 170, N'Renewable Energy ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo Sans''; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Encompass initiatives dedicated to advancing and deploying renewable energy sources like solar, wind, hydropower, and geothermal energy. These projects involve the development, installation, and operation of renewable energy infrastructure such as solar farms, wind turbines, hydroelectric dams, and geothermal power plants. By harnessing natural energy resources, these initiatives aim to diversify energy portfolios, reduce dependence on fossil fuels, mitigate greenhouse gas emissions, and promote sustainable energy generation. Additionally, they contribute to local economic growth, job creation, and technological innovation in the renewable energy sector.</span></p>', NULL, N'#ef4dc5', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2582, 14, 171, N'Carbon Capture and Storage ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo Sans''; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">A strategy that intersects with forestry practices to combat climate change. By capturing carbon dioxide emissions from industrial processes or power generation, CCS plays a vital role in reducing greenhouse gas emissions. In forestry, CCS can complement efforts such as reforestation and sustainable forest management by sequestering carbon in trees and soil. Additionally, forestry-based CCS projects may involve capturing CO2 emissions from biomass utilization processes, such as bioenergy production or wood processing, and storing them underground. This integrated approach harnesses the carbon sequestration potential of forests while simultaneously addressing emissions from other sectors, contributing to overall climate mitigation efforts.</span></p>', NULL, N'#a840c6', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2583, 14, 171, N'Methane Capture and Utilization ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo Sans''; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Focuses on mitigating emissions by capturing methane, a potent greenhouse gas, from various sources like landfills or agricultural activities. Instead of releasing methane into the atmosphere, this strategy involves capturing it and repurposing it for energy generation or other beneficial uses. Projects under this strategy may include the installation of methane capture systems at landfill sites or anaerobic digesters on farms to convert methane into renewable energy sources like biogas. By preventing methane emissions and utilizing them for productive purposes, this approach contributes to reducing greenhouse gas emissions and promoting sustainable energy practices.</span></p>', NULL, N'#ce0098', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2584, 14, 171, N'Transportation Emission Reduction ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo Sans''; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Aims to decrease greenhouse gas emissions originating from transportation activities. This strategy involves implementing various measures such as adopting fuel-efficient technologies, promoting the use of alternative fuels like electricity or hydrogen, and encouraging sustainable transportation practices such as carpooling, biking, or using public transit. Projects under this strategy may include initiatives to retrofit existing vehicles with cleaner technologies, establish infrastructure for electric vehicle charging stations, develop incentives for eco-friendly transportation choices, and enhance public transportation networks.</span></p>', NULL, N'#dd246a', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2585, 14, 172, N'Climate Change Mitigation ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo Sans''; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Entails deploying targeted ecological research and resource management tactics to curtail greenhouse gas emissions and counter climate change effects. This strategy focuses on employing ecological studies to better understand carbon dynamics, ecosystem resilience, and biodiversity conservation. By identifying ecosystems'' roles in carbon storage and sequestration&mdash;such as forests, wetlands, and oceans&mdash;this approach emphasizes restoring and conserving these natural sinks. Specific projects might include reforestation and afforestation efforts, peatland restoration, and sustainable land management practices that enhance soil carbon sequestration. Ecological research also guides the development of green infrastructure in urban settings to reduce carbon footprints, alongside studying the impacts of climate change on biodiversity to inform conservation strategies. This scientifically informed pathway ensures that mitigation efforts are both effective in reducing atmospheric CO2 levels and beneficial for ecosystem health and biodiversity, aligning climate action with ecological preservation.</span></p>', NULL, N'#ad456d', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2586, 14, 172, N'Sustainable Resource Management ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo Sans''; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Involves the responsible and efficient use of natural resources to meet current needs without compromising the ability of future generations to meet their own needs.</span></p>', NULL, N'#a20051', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2587, 14, 172, N'Traditional Ecological Knowledge Practices ', N'<p>F<span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo Sans''; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">ocusing on Traditional Ecological Knowledge (TEK) within applied science and resource management involves a blend of research projects and training programs aimed at integrating indigenous wisdom with contemporary environmental science. Research projects encompass ethnobotanical studies documenting traditional plant uses, climate change adaptation studies examining indigenous coping mechanisms, and wildlife management research highlighting sustainable hunting and fishing practices. Training programs include TEK workshops for ecologists, indigenous youth in science initiatives, cross-cultural knowledge exchange conferences, and community-based monitoring initiatives. These efforts aim to nurture a deeper understanding and respect for indigenous knowledge, fostering collaboration between indigenous communities, scientists, and policymakers to promote biodiversity conservation, sustainable resource management, and the revitalization of indigenous cultures.</span></p>', NULL, N'#8e0061', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2588, 14, 173, N'Field Surveys ', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo Sans''; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Entails systematic data collection through firsthand observation, measurements, and sampling conducted in natural environments. These surveys encompass a range of activities such as biodiversity assessments to understand species composition and distribution, vegetation surveys to analyze plant communities and ecological patterns, and soil sampling to examine soil properties and nutrient levels. By gathering data directly from the field, researchers can obtain accurate information essential for ecological research, resource management, and conservation efforts.</span></p>', NULL, N'#9f5ada', NULL)
GO
INSERT [dbo].[TaxonomyLeaf] ([TaxonomyLeafID], [TenantID], [TaxonomyBranchID], [TaxonomyLeafName], [TaxonomyLeafDescription], [TaxonomyLeafCode], [ThemeColor], [TaxonomyLeafSortOrder]) VALUES (2589, 14, 173, N'Remote-Sensing and GIS Based Research', N'<p><span style="font-size: 10.5pt; line-height: 107%; font-family: ''Azo Sans''; mso-fareast-font-family: Aptos; mso-fareast-theme-font: minor-latin; mso-bidi-font-family: ''Times New Roman''; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Involves remote-sensing and GIS-based techniques in applied science and research entail the use of advanced technologies like satellites and aerial platforms, coupled with Geographic Information System (GIS) tools, to monitor and analyze environmental changes over large spatial scales. These methods enable researchers to collect data remotely, allowing for the mapping of land cover changes, assessment of habitat fragmentation, and monitoring of ecological trends using satellite imagery and geospatial data analysis. By leveraging these technologies, scientists can gain valuable insights into ecosystem dynamics, land use patterns, and environmental changes, facilitating informed decision-making in resource management and conservation initiatives.</span></p>', NULL, N'#c420ad', NULL)
GO
